import React, { useContext, useRef, useEffect, useState } from "react";
import "../../../Scss/Admins.scss";
import PhotoGame from "../../../Images/icon-wz-white.png";
import Recycle from "../../../Images/delete.png";
import Plus from "../../../Images/plus.png";
import Popup from "reactjs-popup";
import "reactjs-popup/dist/index.css";
import WZon from "../../../Images/wz_logogold.png";
import Valorant from "../../../Images/valorant.png";
import Apex from "../../../Images/Apex.png";
import Dota from "../../../Images/dotalogo.png";
import Fifa from "../../../Images/whiteFifa.png";
import SelectMethod from "./GameIdTools/SelectMethod";
import TestContext from "../../../Context/testContext";
import AcceptMethod from "./GameIdTools/AcceptMethod";
import FaildMethod from "./GameIdTools/FaildMethod";



function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const GameId = () => {
  const testContext = useContext(TestContext);
  const {
    ShowGameItem,
    SetShowGamelItems,
    checkUserData,
    CheckUserExist,
    gameIdInfoData,
    GetAllUserAccounts,
    DeleteAccountOfUser,
    deleteGameIdData,
    err,
  } = testContext;

  const [FlowState, setFlowState] = useState(1);
  const [ErrorId, setErrorId] = useState(null);
  const [UserAccunts, setEUserAccunts] = useState([]);

  useEffect(() => {
    GetAllUserAccounts();
  }, []);

  useEffectSkipFirst(() => {
    if (checkUserData) {
      setErrorId(0);
      setFlowState(3);
      GetAllUserAccounts();
      console.log("check", checkUserData);
    }
  }, [checkUserData]);

  useEffectSkipFirst(() => {
    if (err) {
      setErrorId(1);
      setFlowState(3);
    }
  }, [err]);

  useEffectSkipFirst(() => {
    if (deleteGameIdData) {
      GetAllUserAccounts();
      console.log("del", deleteGameIdData);
    }
  }, [deleteGameIdData]);

  useEffectSkipFirst(() => {
    if (gameIdInfoData) {
      let newData = gameIdInfoData.map((item) => {
        let newItem = {};
        newItem.Id = item.Id;
        newItem.typedesc = item.typedesc;
        newItem.User = item.User;
        newItem.GameId = item.GameId;
        newItem.Country = item.Country;
        return newItem;
      });
      console.log("gamedata", newData);
      setEUserAccunts(newData);
    }
  }, [gameIdInfoData]);
  const sendDelete = (Id) => {
    let newItem = {};
    newItem.Id = Id;
    DeleteAccountOfUser(newItem);
  };

  return (
    <div className="GameId">
      <div className="Title">
        <h3>Game IDs</h3>
        <h6>Game accounts l've connected</h6>
      </div>
      {UserAccunts.map((item) => (
        <div className="BoxTrans" key={item.Id}>
          <div className="GameInfo">
            <img
              src={
                item.GameId == 1
                  ? PhotoGame
                  : item.GameId == 2
                  ? Dota
                  : item.GameId == 3
                  ? Fifa
                  : item.GameId == 4
                  ? Valorant
                  : item.GameId == 5
                  ? Apex
                  : null
              }
              alt="Game"
            />
            <div className="infoText">
              <h5>{item.User}</h5>
              <h5>
                <span>{item.Country}</span> {item.typedesc}
              </h5>
            </div>
          </div>
          {item.Id ? (
            <div
              className="trash"
              onClick={(e) => {
                sendDelete(item.Id);
              }}
            >
              <img src={Recycle} alt="" />
            </div>
          ) : null}
        </div>
      ))}
      <div className="BoxTrans">
        <div className="GameInfo">
        <Popup
          trigger={
            <div className="plus">
              <img onClick={(e) => setFlowState(1)} src={Plus} alt="Recycle" />
            </div>
          }
          modal
        >
          {FlowState == 1 ? (
            <div className="addGameIdBox">
              <div className="headBoxId">
                <h2>Game Account</h2>
              </div>
              <div className="noticeId">
                <h5>
                  Please select the game that you want to connect. you can add
                  more than one account for each game title.
                </h5>
              </div>
              <div className="boxSelectGame">
                <div
                  onClick={(e) => {
                    setFlowState(2);
                    SetShowGamelItems(1);
                  }}
                  className="box"
                >
                  <img src={WZon} alt="WZon" />
                </div>
                <div
                  onClick={(e) => {
                    SetShowGamelItems(4);
                  }}
                  className="box"
                >
                  <img src={Valorant} alt="Valorant" />
                </div>
                <div
                  onClick={(e) => {
                    setFlowState(2);
                    SetShowGamelItems(5);
                  }}
                  className="box"
                >
                  <img src={Apex} alt="Apex" />
                </div>
                <div
                  onClick={(e) => {
                    setFlowState(2);
                    SetShowGamelItems(2);
                  }}
                  className="box"
                >
                  <img className="Dota" src={Dota} alt="Dota" />
                </div>
                <div onClick={(e) => SetShowGamelItems(3)} className="box">
                  <img src={Fifa} alt="Fifa" />
                </div>
              </div>
            </div>
          ) : null}

          {FlowState == 2 ? (
            <>
              {ShowGameItem == 1 ? (
                <SelectMethod logo={WZon} Name={Call} />
              ) : null}
              {ShowGameItem == 4 ? (
                <SelectMethod logo={Valorant} Name={ValorantN} />
              ) : null}
              {ShowGameItem == 5 ? (
                <SelectMethod logo={Apex} Name={ApexN} />
              ) : null}
              {ShowGameItem == 2 ? (
                <SelectMethod logo={Dota} Name={Dota2} />
              ) : null}
              {ShowGameItem == 3 ? (
                <SelectMethod logo={Fifa} Name={FiFa23} />
              ) : null}
            </>
          ) : null}
          {FlowState == 3 ? (
            <>
              {ErrorId == 0 ? <AcceptMethod logo={WZon} Name={Call} /> : null}
              {ErrorId == 1 ? <FaildMethod logo={WZon} Name={Call} /> : null}
            </>
          ) : null}
        </Popup>
          <img className="Plus" src={Plus} alt="Game" />
          <div className="infoText">
            <h5>ADD ACCOUNT</h5>
            <h5>click here to add account</h5>
          </div>
        </div>
        <div className="trash"></div>
      </div>
    </div>
  );
};

export default GameId;
