import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";


function useEffectSkipFirst(fn, arr) {
    const isFirst = useRef(true);
    useEffect(() => {
      if (isFirst.current) {
        isFirst.current = false;
        return;
      }
      fn();
    }, arr);
  }

const Location = () => {
  const testContext = useContext(TestContext);
  const [country, setCountry] = useState(null);
  const authContext = useContext(AuthContext);
  const { user, login, isAuthenticated, logout } = authContext;

  const {
    GetCountries,
    countriesData,
    userInfoData,
    GetUserInfo,
  } = testContext;

  const [DataUser, setDataUser] = useState(null);


  useEffect(() => {
    GetCountries();
    GetUserInfo();
  }, []);

  const sendData = () => {
    let newUser = {};

    newUser.Country = country;


  };

  useEffectSkipFirst(() => {
    if (userInfoData) {
        let newItem = {};
        newItem.CountryId = userInfoData.CountryId;
        newItem.City = userInfoData.City;


        // return newItem;
      setDataUser(newItem);

    }
  }, [userInfoData]);




  return (
    <div className="Setiings">
      <div className="Title">
        <h4>Your Location</h4>
      </div>
      <div className="PersonalEdit">
        <div className="item">
          <h5>Country</h5>
          <select
            onChange={(e) => {
              setCountry(e.target.value);
            }}
          >
            {countriesData
              ? countriesData.map((item) => {
                  return (
                    <option value={item.id + ":" + item.Country}>
                      {item.Country}
                    </option>
                  );
                })
              : []}
          </select>
        </div>
        <div className="item">
          <h5>City</h5>
          <input type="password" value={DataUser?DataUser.City:''} />
        </div>
        <div className="itemEtra">
          <button>Save Changes</button>
        </div>
      </div>
    </div>
  );
};

export default Location;
