import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../Context/testContext";
import AuthContext from "../../Context/Auth/authContext";
import "../../Scss/Firstpage.scss";
import Coin from "../../Images/coins.png";
import { NavLink } from "react-router-dom";
import Swal from "sweetalert2";
import "sweetalert2/src/sweetalert2.scss";

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const BoxIvonet = ({ gameId }) => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const { user, login, isAuthenticated, logout } = authContext;

  const [IdGame, setIdGame] = useState(null);
  const {
    GetActiveTournaments,
    activeTournamentData,
    registerGameUserData,
    RegisterUserForGame,
    SetRegisterTournament,
    err,
  } = testContext;
  const [gameUserdata, setgameUserdata] = useState(null);
  const [GameId, setGameId] = useState(null);
  const [TournamentId, setTournamentId] = useState(null);
  const [showMessage, setShowMessage] = useState(null);


  useEffect(() => {
    setIdGame(gameId);
    GetActiveTournaments(gameId);
  }, []);

  useEffectSkipFirst(() => {
    if (gameId) {
      GetActiveTournaments(gameId);
    }
  }, [gameId]);

  useEffectSkipFirst(() => {
    if (startTournmentData) {
      if (startTournmentData.userError) {
        Swal.fire("you dont have userId", "Please check your info", "warning");
      }
      // Swal.fire({
      //   position: "center",
      //   icon: "success",
      //   title: "your tournment has been started",
      //   timer: 2000,
      // });
      else {
        setShowMessage(true);
        SetRegisterTournament(null);
      }
    }
  }, [startTournmentData]);

  // useEffectSkipFirst(() => {
  //   if (registerGameUserData) {
  //     if (registerGameUserData.userError) {
  //       Swal.fire(
  //         "you dont have userId",
  //         "you must Add your Game id in My profile page",
  //         "warning"
  //       );
  //     } else
  //       Swal.fire({
  //         position: "center",
  //         icon: "success",
  //         title: "Your game has been pushed",
  //         timer: 2000,
  //       });

  //     console.log("why");
  //   }
  // }, [registerGameUserData]);




  const SendInfo = (id) => {
    let gameData = {};
    gameData.GameId = gameId;
    gameData.TournamentId = parseInt(id);

    RegisterUserForGame(gameData);
  };

  return (
    <div className="ItemMatch">
      {activeTournamentData
        ? activeTournamentData.map((item) => (
            <div id="BoxIvonet" key={item.id}>
              <div className="titleIvonet">
                <h4>{item.Title}</h4>
              </div>
              <div className="itemMatch">
                <label>
                  {" "}
                  {item.StartDate} - {item.EndDate}
                </label>
              </div>
              <div className="itemMatch">
                <label>PRIZE</label>
                <label className="primary">{item.EntryFee}$</label>
              </div>
              <div className="itemMatch">
                <label>SCORING</label>
                <label className="primary">BEST OF {item.Winning} GAME</label>
              </div>
              <div className="itemMatch">
                <label>DIVISIONS</label>
                <label className="primary">{item.KDLimit}</label>
              </div>
              <div className="itemMatch">
                <div className="priceCoin">
                  <div className="coinImg">
                    <img src={Coin} alt="Coin" />
                  </div>
                  <label>{item.Prize}</label>
                </div>
                {user ? (
                  <button
                    id={item.id}
                    onClick={(e) => {
                      SendInfo(e.target.id);
                    }}
                  >
                    {" "}
                    REGISTER
                  </button>
                ) : (
                  <NavLink to="/Login">
                    <button> REGISTER</button>
                  </NavLink>
                )}
              </div>
            </div>
          ))
        : null}
    </div>
  );
};

export default BoxIvonet;
