import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../../Context/testContext";
import Swal from 'sweetalert2/dist/sweetalert2.js'
import 'sweetalert2/src/sweetalert2.scss'

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}
const ActiveTpurnament = () => {
  useEffect(() => {
    GetActiveTournaments(0);
  }, []);
  const testContext = useContext(TestContext);
  const {
    GetActiveTournaments,
    activeTournamentData,
    deletedTournamentData,
    GetPreviousTournaments,
    DeleteTournament,
    err,
  } = testContext;

  useEffectSkipFirst(() => {
    if (deletedTournamentData) {
      GetActiveTournaments();
    }
  }, [deletedTournamentData]);


  const delteItem = (itemId) => {

    Swal.fire({
      title: 'Are you sure?',
      text: "You won't be able to revert this!",
      icon: 'warning',
      showCancelButton: true,
      confirmButtonColor: '#3085d6',
      cancelButtonColor: '#d33',
    }).then((result) => {
      if (result.isConfirmed) {
        DeleteTournament(itemId);
      }
    })


  };

  useEffectSkipFirst(() => {
    if (deletedTournamentData) {
      Swal.fire({
        text: " Your file has been deleted.",
        icon: "success",
      }).then((result) => {

      });
      GetActiveTournaments();
    }
  }, [deletedTournamentData]);

  const NameGameById =(id)=>{
    switch (id) {
      case 1:
        return "WARZON"
        break;
      case 2:
        return "DOTA2"
        break;

      default:
        break;
    }
  }




  return (
    <div className="ActiveTpurnament">
      <div className="HeadCreate">
        <h4>Active Tournament</h4>
      </div>
      {activeTournamentData
        ? activeTournamentData.map((item) => (
            <div key={item.id} className="ActiveAdd">
              <h5>{item.Title}</h5>
              <div className="boxAdd">
                <h6>{NameGameById(item.GameId)}</h6>
                <h6 className="larghx">
                  {item.StartDate} - {item.EndDate}
                </h6>
                <h6 className="largh">{item.KDLimit}</h6>

                <h6>{item.Prize}$</h6>
                <h6>{item.EntryFee  ? item.EntryFee == 0 || !item.Prize : "Free"}FLC</h6>
                <div className="buttonsDiv">
                  <button>See result</button>
                  <button>Edit</button>
                  <button
                    onClick={(e) => {
                      delteItem({ Id: item.id });
                    }}
                  >
                    Remove
                  </button>
                </div>
              </div>
            </div>
          ))
        : null}

    </div>
  );
};

export default ActiveTpurnament;
