import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../Context/testContext";
import AuthContext from "../../Context/Auth/authContext";
import "../../Scss/Firstpage.scss";
import Coin from "../../Images/coins.png";
import { NavLink } from "react-router-dom";


function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const BoxIvonet = ({ gameId }) => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const { user, login, isAuthenticated, logout } = authContext;

  const [IdGame , setIdGame]=useState(null);
  const {
    GetActiveTournaments,
    activeTournamentData,
    registerGameUserData,
    RegisterUserForGame,
    err,
  } = testContext;

  useEffect(() => {
    setIdGame(gameId);
    GetActiveTournaments(gameId);
  }, []);
  

  useEffectSkipFirst(() => {
    if (gameId) {
      GetActiveTournaments(gameId);
    }
  }, [gameId]);


  const SendInfo = () => {
    let prsData = {};

    prsData.PrsNum = PrsNum;
    if (persondata) {
      prsData.Nam = persondata.Nam;
      prsData.NamKhanevadegi = persondata.NamKhanevadegi;
    }
    prsData.Title = Title;
    prsData.Goal = Goal;
    prsData.InnovLevel = InnovLevel;
    prsData.Discription = Description;
    prsData.ProjectName = ProjectName;
    if (Title && Goal && InnovLevel && ProjectName) {
      if (
        Title.length > 1 &&
        Goal.length > 1 &&
        InnovLevel.length > 1 &&
        ProjectName.length > 1
      ) {
        CreateOrUpdateInnovationEvent(prsData);
        // console.log('Kode',PrsCode );
        // Swal.fire(
        //   "نوآوری شما با موفقیت ثبت شد",
        //   "نتیجه نوآوری شما بعد از بررسی های لازم اعلام خواهد شد",
        //   "success"
        // ).then((result) => {
        //   /* Read more about isConfirmed, isDenied below */
        //   if (result.isConfirmed) {
        //     window.location.reload();
        //     window.location.scrollTo();
        //   }
        // });
      }
    } else {
      console.log("Test");
      Swal.fire("اشتباه", "تمام فیلد های اطلاعاتی باید پر شوند", "error");
    }
  };


  return (
    <div className="ItemMatch">
        {activeTournamentData
        ? activeTournamentData.map((item) => (
      <div id="BoxIvonet" key={item.id}>
        <div className="titleIvonet">
          <h4>{item.Title}</h4>
        </div>
        <div className="itemMatch">
          <label> {item.StartDate} - {item.EndDate}</label>
        </div>
        <div className="itemMatch">
          <label>PRIZE</label>
          <label className="primary">{item.EntryFee}$</label>
        </div>
        <div className="itemMatch">
          <label>SCORING</label>
          <label className="primary">BEST OF {item.Winning} GAME</label>
        </div>
        <div className="itemMatch">
          <label>DIVISIONS</label>
          <label className="primary">{item.KDLimit}</label>
        </div>
        <div className="itemMatch">
          <div className="priceCoin">
            <div className="coinImg">
              <img src={Coin} alt="Coin" />
            </div>
            <label>{item.Prize}</label>
          </div>
          {user ? <button> REGISTER</button> :( <NavLink to="/Login"><button> REGISTER</button></NavLink>)}
          
        </div>
      </div>
        )):null}
    </div>
  );
};

export default BoxIvonet;
