import React, { useEffect, useRef, useContext, useState } from "react";
import { Calendar, utils } from "react-modern-calendar-datepicker";
import DatePicker from "@hassanmojab/react-modern-calendar-datepicker";
// import DatePicker from "react-modern-calendar-datepicker";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";
import "../../../Scss/Manage.scss";
import Allert from "../../../Images/Attention.png";



function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);

  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}
const EditSolo = ({ Id }) => {



  const myCustomLocale = {
    // months list by order
    months: [
      'January',
      'February',
      'March',
      'April',
      'May',
      'June',
      'July',
      'August',
      'September',
      'October',
      'November',
      'December',
    ],

    // week days by order
    weekDays: [
      {
        name: 'Sunday', // used for accessibility 
        short: 'S', // displayed at the top of days' rows
        isWeekend: true, // is it a formal weekend or not?
      },
      {
        name: 'Monday',
        short: 'M',
      },
      {
        name: 'Tuesday',
        short: 'T',
      },
      {
        name: 'Wednesday',
        short: 'W',
      },
      {
        name: 'Thursday',
        short: 'T',
      },
      {
        name: 'Friday',
        short: 'F',
      },
      {
        name: 'Saturday',
        short: 'S',
        isWeekend: true,
      },
    ],

    // just play around with this number between 0 and 6
    weekStartingIndex: 0,

    // return a { year: number, month: number, day: number } object
    getToday(gregorainTodayObject) {
      return gregorainTodayObject;
    },

    // return a native JavaScript date here
    toNativeDate(date) {
      return new Date(date.year, date.month - 1, date.day);
    },

    // return a number for date's month length
    getMonthLength(date) {
      return new Date(date.year, date.month, 0).getDate();
    },

    // return a transformed digit to your locale
    transformDigit(digit) {
      return digit;
    },

    // texts in the date picker
    nextMonth: 'Next Month',
    previousMonth: 'Previous Month',
    openMonthSelector: 'Open Month Selector',
    openYearSelector: 'Open Year Selector',
    closeMonthSelector: 'Close Month Selector',
    closeYearSelector: 'Close Year Selector',
    defaultPlaceholder: 'Select...',

    // for input range value
    from: 'from',
    to: 'to',


    // used for input value when multi dates are selected
    digitSeparator: ',',

    // if your provide -2 for example, year will be 2 digited
    yearLetterSkip: 0,

    // is your language rtl or ltr?
    isRtl: false,
  }
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const { SetSwitchUsers, GetTournamentById, tournamentByIdData } = testContext;
  const [data, setData] = useState(null);

  useEffect(() => {
    // registerLocale("ja", ja);
    console.log("tourid", Id);
    // setShow(false);
    GetTournamentById(Id);
    // setTimeout(() => {
    //   setShow(true);

    // }, 2);
  }, []);


  useEffectSkipFirst(() => {
    if (tournamentByIdData) {
      // console.log("tourdata111:", tournamentByIdData);
      setData(tournamentByIdData);
      if (data) {

        setTitle(data.Title);
        setSelectedMode(data.ModeId);
        setMinutes(data.Minutes);
        setEntryFee(data.EntryFee);
        setPrize(data.Prize);
        setAbout(data.About);
        let fromData = {};
        let toData = {};
        if (data.StartDate) {
          let dateStr = data.StartDate.split("-");
          let year = dateStr[0];
          let month = dateStr[1];
          let day = dateStr[2];
          console.log("startdate", year, month, day);
          fromData.year = year;
          fromData.month = month;
          fromData.day = day;
        }
        if (data.EndDate) {
          let dateStr = data.EndDate.split("-");
          let year = dateStr[0];
          let month = dateStr[1];
          let day = dateStr[2];

          console.log("EndDate", year, month, day);
          toData.year = year;
          toData.month = month;
          toData.day = day;
        }
        setSelectedDayRange({
          from: fromData,
          to: toData,
        });

      }
    }
  }, [tournamentByIdData]);
  useEffectSkipFirst(() => {
