      if (data.EndDate) {
        let dateStr = data.EndDate.split("-");
        let year = dateStr[0];
        let month = dateStr[1];
        let day = dateStr[2];
        toData.year = year;
        toData.month = month;
        toData.day = day;
      }
      setSelectedDayRange({
        from: fromData,
        to: toData,
      });

    }
  }, [data]);
