              onChange={(e) => setMinutes(e.target.value)}
              onWheel={(e) => e.target.blur()}
            />
          </div>

          <div className="item">
            <h5>Entry Fee</h5>
            <div className="inputWsymbol">
              <input
                value={entryFee}
                onChange={(e) => setEntryFee(e.target.value)}
                type="number"
                onWheel={(e) => e.target.blur()}
              />
              <h6>FLC Coin</h6>
            </div>
          </div>
          <div className="item">
            <h5>Prize</h5>
            <div className="inputWsymbol">
              <input
                value={prize}
                onChange={(e) => setPrize(e.target.value)}
                type="number"
                onWheel={(e) => e.target.blur()}
              />
              <h6>$</h6>
            </div>
          </div>
        </div>
        <div className="TextBoxSection">
          <div className="item">
            <h5>About Tournament</h5>
            <textarea
              className="BoxInfo"
              value={About}
              onChange={(e) => setAbout(e.target.value)}
            ></textarea>
          </div>
        </div>
        <div className="itemBtn">
          <button
            className="btnCreate"
          // onClick={(e) => {
          //   sendDataToCreateTournament();
          // }}
          >
            Close
          </button>
          <button
            className="btnCreate"
          // onClick={(e) => {
