import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const ResultSolo = () => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const { GetRankAndPointOfTournament, rankAndPoinOfTournaments, err } =
    testContext;
  const [TournamentScore, setTournamentScore] = useState([]);

  useEffect(() => {
    GetRankAndPointOfTournament(58);
  }, []);

  useEffectSkipFirst(() => {
    if (rankAndPoinOfTournaments) {
      let newData = rankAndPoinOfTournaments.map((item) => {
        let newItem = {};
        newItem.id = item.id;
        newItem.FinalPoint = item.FinalPoint;
        newItem.UserName = item.UserName;

        return newItem;
      });
      setTournamentScore(newData);
    }
  }, [rankAndPoinOfTournaments]);

  return (
    <div className="RoulsSolo">
      <div className="title">
        <h4>Placement</h4>
        <h4>ID</h4>
        <h4></h4>
        <h4></h4>
        <h4>View Details</h4>
      </div>
      {TournamentScore.map((item) => (
        <div className="Row">
          <h4>1</h4>
          <h4>{item.UserName}</h4>
          <h4>Remove</h4>
          <h4>243.4</h4>
          <h4>View Details</h4>
        </div>
      ))}

      <div className="pagination">
        <h6>←</h6>
        <h6>1</h6>
        <h6>2</h6>
        <h6>3</h6>
        <h6>4</h6>
        <h6>5</h6>
        <h6>→</h6>
      </div>
    </div>
  );
};

export default ResultSolo;
