import React, { useContext, useRef, useEffect, useState } from "react";
import Coin from "../../../Images/coins.png";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";
import Swal from "sweetalert2";
import "sweetalert2/src/sweetalert2.scss";

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const PersonalInfo = () => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const {
    personalInfoData,
    switchBetweenUserAndPersonalInfo,
    SetSwitchUsers,
    usersEditByAdmin,
    UpdateUsersByAdmin,
    allUserFalconData,
    err,
  } = testContext;

  const [Firstname, setFirstname] = useState(null);
  const [Username, setUsername] = useState(null);
  const [Birthdate, setBirthdate] = useState(null)
  const [showMessage, setShowMessage] = useState(null);

  const [City, setCity] = useState(null);

  useEffectSkipFirst(() => {
    if (personalInfoData) {
      let newItem = {};
      setFirstname(personalInfoData.Firstname);
      setBirthdate(personalInfoData.Birthdate);
      setUsername(personalInfoData.Username);
      setCity(personalInfoData.City);
    }
  }, [personalInfoData]);

  const sendUserData = () => {
    let newUser = {};
    if (Firstname.length > 3 && Username.length > 3) {
      newUser.FirstName = Firstname;
      newUser.Birthdate = Birthdate;
      newUser.Username = Username;
      newUser.City = City;

      UpdateUsersByAdmin(newUser);
      // console.log("Test",newUser);

    } else {
      console.log("empty");
    }

    // console.log("1", newUser);
  };


  useEffectSkipFirst(() => {
    if (showMessage) {
      Swal.fire({
        position: "center",
        icon: "success",
        title: "Your Information has been Updated",
        showConfirmButton: false,
        timer: 1500,
      });
      setShowMessage(false);
    }
  }, [showMessage]);

  useEffectSkipFirst(() => {
    if (usersEditByAdmin) {
        if(usersEditByAdmin.status == true){

          setShowMessage(true);
          SetUserSetting(null);
        } 

    }
  }, [usersEditByAdmin]);

  return (
    <>
      {switchBetweenUserAndPersonalInfo ? null : (
        <div className="PersonalInfo">
          <div className="back">
            <h2>{personalInfoData.Username}</h2>
            <h2
              className="backStyle"
              onClick={(e) => {
                SetSwitchUsers(true);
              }}
            >
              Back
            </h2>
          </div>
          <div className="InputInfo">
            <h4>Personal Information</h4>
            <div className="prsData">
              <div className="item">
                <h5>Full Name</h5>
                <input
                  type="text"
                  value={Firstname}
                  onChange={(e) => setFirstname(e.target.value)}
                />
              </div>
              <div className="item">
                <h5>Date of Birth</h5>

                <input
                  type="Date"
