      GetAllUserFalcon();
    }, []);

    useEffectSkipFirst(() => {
      if (allUserFalconData) {
        let newItem = {};
        console.log(allUserFalconData)

      }
    }, [allUserFalconData]);


  return (
    <div className='UserInfo'>
      {
        (showorhide)?
      <>        <div className='SearchTitle'>
        <h3>Users</h3>
        <div className='search'>
        <img src={Search} alt="Search"/>
        <input type="search"/>
        </div>
        </div>
        {allUserFalconData ?allUserFalconData.data ?allUserFalconData.data.map((item)=> {

       return  <div className="userData">
            <h4>{item.Username}</h4>
            <a  id={item.id} onClick={(e)=>{console.log(item)}} >Show / Edit</a>
        </div>
        }):null:null}
        </>:null
}

    </div>
  )
}

export default UserInfo
