import React, { useContext, useRef, useEffect, useState } from "react";
import Search from '../../../Images/icons8-search-24.png';
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";


function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const UserInfo = () => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const { user, login, isAuthenticated, logout } = authContext;
  const [showorhide,setShoworhide]=useState(null);


  const { allUserFalconData,GetAllUserFalcon, err } =
    testContext;

    useEffect(() => {
      GetAllUserFalcon();
    }, []);

    useEffectSkipFirst(() => {
      if (allUserFalconData) {
        setShoworhide(true);
        let newItem = {};
        console.log(allUserFalconData)

      }
    }, [allUserFalconData]);


  return (
    <div className="UserInfo">
      {showorhide ? (
        <>
          {" "}
          <div className="SearchTitle">
            <h3>Users</h3>
            <div className="search">
              <img src={Search} alt="Search" />
              <input type="search" />
            </div>
          </div>
          {allUserFalconData
            ? allUserFalconData.data
              ? allUserFalconData.data.map((item) => {
                  return (
                    <div className="userData">
                      <h4>{item.Username}</h4>
                      <a
                        id={item.id}
                        onClick={(e) => {
                          console.log(item);
                          setShoworhide(true);
                        }}
                      >
                        Show / Edit
                      </a>
                    </div>
                  );
                })
              : null
            : null}
        </>
      ) : null}
    </div>
  );
}

export default UserInfo
