import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../../Context/testContext";
import "@hassanmojab/react-modern-calendar-datepicker/lib/DatePicker.css";
import DatePicker from "@hassanmojab/react-modern-calendar-datepicker";
import Loadingspiner from "../../../Images/Spinner.gif";
import Swal from 'sweetalert2/dist/sweetalert2.js'
import 'sweetalert2/src/sweetalert2.scss';
import { Calendar, utils } from "react-modern-calendar-datepicker";


function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const CreateTournamentAd = () => {
  const testContext = useContext(TestContext);
  const [showMessage, setShowMessage] = useState(null);
  const [selectedGame, setSelectedGame] = useState(null);
  const [selectedMode, setSelectedMode] = useState(null);
  const [selectedKD, setSelectedKD] = useState(null);
  const [entryFee, setEntryFee] = useState(null);
  const [prize, setPrize] = useState(null);
  const [title, setTitle] = useState(null);
  const [Minutes, setMinutes] = useState(null);
  const [About, setAbout] = useState(null);
  const [Winning, setWinning] = useState(null);
  const [Loading, setLoading] = useState(false);
  const {
    createTournamentData,
    CreateTournament,
    GetActiveTournaments,
    activeTournamentData,
    previousTournamentData,
    deletedTournamentData,
    GetPreviousTournaments,
    SetCreateTournmant,
    err,
  } = testContext;

  // ✅ a change in default state: { from: ..., to: ... }
  const [selectedDayRange, setSelectedDayRange] = useState({
    from: null,
    to: null,
  });

  const sendDataToCreateTournament = (e) => {
    if (
      selectedDayRange.from &&
      selectedDayRange.to &&
      selectedGame > 0 &&
      selectedMode > 0 &&
      selectedKD > 0 &&
      entryFee.length > 0 &&
      prize &&
      title.length > 0
    ) {
      let newTournament = {};
      newTournament.StartDate =
        selectedDayRange.from.year +
        "-" +
        selectedDayRange.from.month +
        "-" +
        selectedDayRange.from.day;
      newTournament.EndDate =
        selectedDayRange.to.year +
        "-" +
        selectedDayRange.to.month +
        "-" +
        selectedDayRange.to.day;
      newTournament.GameId = selectedGame;
      newTournament.ModeId = selectedMode;
      newTournament.KDLimit = selectedKD;
      newTournament.EntryFee = entryFee;
      newTournament.Prize = prize;
      newTournament.Title = title;
      newTournament.Minutes = Minutes;
      newTournament.About = About;
      newTournament.Winning = Winning;
      newTournament.selectedKD = selectedKD;
      console.log(newTournament);
      CreateTournament(newTournament);
    } else {
    }
  };

  useEffectSkipFirst(() => {
    if (createTournamentData) {
      GetActiveTournaments();
      setLoading(false);
      setShowMessage(true);
      SetCreateTournmant(null);

    }
  }, [createTournamentData]);

  useEffectSkipFirst(() => {
    if (showMessage) {
      Swal.fire({
        text: " Tournament has been created.",
        icon: "success",
      })
      setShowMessage(false);
    }
  }, [showMessage]);

  const clearInputsForm = () => {
    setSelectedGame(0);
    setTitle("");
    setSelectedMode(0);
    setSelectedKD("");
    setMinutes("");
    setWinning("");
    setAddress("");
    setPostCode("");
    setEditId("");
  };

  return (
    <div className="TournamentAd">
      <div className={Loading ? "loadingPage" : "loadingPageH"}>
        <img src={Loadingspiner} alt="Loading" />
      </div>
      <div className="HeadCreate">Create a new Tournament</div>
      <div className="MainCreate">
        <div className="infoSimple">
          <div className="item">
            <h5>Game</h5>
            <select
              onChange={(e) => {
                if (e.target.value > 0) {
                  setSelectedGame(e.target.value);
                  console.log("game", e.target.value);
                }
              }}
            >
              <option value={0}>Select Game</option>
              <option value={1}>Warzone</option>
              <option value={2}>Dota</option>
              <option value={3}>Fifa</option>
              <option value={4}>Valorant</option>
              <option value={5}>Apex</option>
            </select>
          </div>
          <div className="item">
            <h5>Tournament Title</h5>
            <input
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              type="text"
            />
          </div>
          <div className="item">
            <h5>Mode</h5>
            <select
              onChange={(e) => {
                if (e.target.value > 0) {
                  setSelectedMode(e.target.value);
                }
              }}
            >
              <option value={0}>Select Mode</option>
              <option value={1}>Solo </option>
              <option value={2}>Duos</option>
              <option value={3}>Trios</option>
              <option value={4}>Quad</option>
            </select>
          </div>
          <div className="item">
            <h5>KD Limit</h5>
            <input
              type="number"
              value={selectedKD}
              onChange={(e) => setSelectedKD(e.target.value)}
              min="0"
              max="6"
              maxLength="1"
              onWheel={(e) => e.target.blur()}
            />
          </div>
          <div className="item">
            <h5>Date</h5>
            <DatePicker
              value={selectedDayRange}
              onChange={setSelectedDayRange}
              inputPlaceholder="Select a day range"
              shouldHighlightWeekends
              minimumDate={utils().getToday()}
            />
          </div>
          <div className="item">
            <h5>Time (per hour)</h5>
            <input
              type="number"
              value={Minutes}
              onChange={(e) => setMinutes(e.target.value)}
              onWheel={(e) => e.target.blur()}
            />
          </div>
          <div className="item">
            <h5>Winning Condition</h5>
            <input
              type="number"
              value={Winning}
              onChange={(e) => setWinning(e.target.value)}
              onWheel={(e) => e.target.blur()}
            />
          </div>
          <div className="item">
            <h5>Entry Fee</h5>
            <div className="inputWsymbol">
              <input
                value={entryFee}
                onChange={(e) => setEntryFee(e.target.value)}
                type="number"
                onWheel={(e) => e.target.blur()}
              />
              <h6>FLC Coin</h6>
            </div>
          </div>
          <div className="item">
            <h5>Prize</h5>
            <div className="inputWsymbol">
              <input
                value={prize}
                onChange={(e) => setPrize(e.target.value)}
                type="number"
                onWheel={(e) => e.target.blur()}
              />
              <h6>$</h6>
            </div>
          </div>
        </div>
        <div className="TextBoxSection">
          <div className="item">
            <h5>About Tournament</h5>
            <textarea
              className="BoxInfo"
              value={About}
              onChange={(e) => setAbout(e.target.value)}
            ></textarea>
          </div>
        </div>
        <div className="item">
          <button
            className="btnCreate"
            onClick={(e) => {
              sendDataToCreateTournament();
              setLoading(true);
            }}
          >
            Create
          </button>
        </div>
      </div>
    </div>
  );
};

export default CreateTournamentAd;
