import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../Context/testContext";
import '../../Scss/Firstpage.scss';
import Coin from '../../Images/coins.png';

function useEffectSkipFirst(fn, arr) {
    const isFirst = useRef(true);
    useEffect(() => {
      if (isFirst.current) {
        isFirst.current = false;
        return;
      }
      fn();
    }, arr);
  }

  


const BoxIvonet = ({gameId}) => {
  return (
    <div id='BoxIvonet'>
        <div className="titleIvonet">
            <h4>RESURGENCE RESTART TRIOS</h4>
        </div>
        <div className="itemMatch">
            <label>JUL,11PM - 24JUL,1 PM 23</label>
        </div>
        <div className="itemMatch">
            <label>PRIZE</label>
            <label className='primary'>100$</label>
        </div>
        <div className="itemMatch">
            <label>SCORING</label>
            <label className='primary'>BEST 3 GAMES</label>
        </div>
        <div className="itemMatch">
            <label>DIVISIONS</label>
            <label className='primary'>3,5 AND 5,5 KD</label>
        </div>
        <div className="itemMatch">
            <div className="priceCoin">
                <div className="coinImg">
                    <img src={Coin} alt="Coin" />
                </div>
                    <label>0 FREE</label>
            </div>
            <button> REGISTER</button>
        </div>

    </div>
  )
}

export default BoxIvonet
