import React from "react";
import "../../Scss/Firstpage.scss";

const AboutFalcon = () => {
  return (
    <div id="AboutFalcon">
      <div className="titleInfo">
        <h1>ABOUT FALCON LEAGUE</h1>
      </div>
      <div className="aboutText">
        <p>
          FLC.gg is the ultimate destination for esport and online games
          competition and tournaments. We're committed to providing gamers with
          the best possible experience, whether they're competing for prizes or
          just playing for fun. Our platform is easy to use and we offer a wide
          range of competitive games, so there's something for everyone. With
          FLC.gg, you can always find new challenges and put your skills to the
          test!
        </p>
      </div>
    </div>
  );
};

export default AboutFalcon;
