import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";
import Swal from "sweetalert2";
import "sweetalert2/src/sweetalert2.scss";

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const ChangePass = () => {

  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const { user, login, isAuthenticated, logout } = authContext;

  const {
    UpdateUserPassword,
    updateUserPasswordData,
    SetUserPassword,
    err,
  } = testContext;

  const [Current, setCurrent] = useState(null);
  const [Password, setPassword] = useState(null);
  const [showMessage, setShowMessage] = useState(null);

  const sendUserData = () => {
    let newUser = {};
    newUser.Current = Current;
    newUser.Password = Password;

    UpdateUserPassword(newUser);
  };


  useEffectSkipFirst(() => {
    if (updateUserPasswordData) {
      {
        setShowMessage(true);
        SetUserPassword(null);
      }
    }
  }, [updateUserPasswordData]);


  useEffectSkipFirst(() => {
    if (showMessage) {
      Swal.fire({
        position: "center",
        icon: "success",
        title: "Your Password has been changed",
        showConfirmButton: false,
        timer: 1500,
      });
      setShowMessage(false);
    }
  }, [showMessage]);


  return (
    <div className="Setiings">
      <div className="Title">
        <h4>Your Password</h4>
      </div>
      <div className="PersonalEdit">
        <div className="item">
          <h5>Current Password</h5>
          <input type="password" value={Cu} />
        </div>
        <div className="item">
          <h5>New Password</h5>
          <input type="password" />
        </div>
        <div className="item">
          <h5>Confirm New Password</h5>
          <input type="password" />
        </div>
      </div>
      <div className="buttonSetting">
        <button>Save Changes</button>
      </div>
    </div>
  );
};

export default ChangePass;
