import React, { useContext, useRef, useEffect, useState } from "react";
import Warzone from "../../../Images/icon-wz-white.png";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const PastTour = ({ Logo, logoStyle }) => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const { GetPreviousTournamentsById, perviousDataTournamentsUser, err } =
    testContext;
  const [Past, setTournamentScore] = useState([]);


  useEffect(() => {
    GetPreviousTournamentsById(27);
  }, []);

  useEffectSkipFirst(() => {
    if (perviousDataTournamentsUser) {
      let newData = perviousDataTournamentsUser.map((item) => {
        let newItem = {};
        newItem.id = item.id;
        newItem.FinalPoint = item.FinalPoint;
        newItem.User = item.User;
        newItem.TournamentId = item.TournamentId;
        newItem.Prize = item.Prize;
        setTornId(item.TournamentId)
        
        return newItem;
    });
    setTournamentScore(newData);
    }
  }, [perviousDataTournamentsUser]);

  return (
    <div className="PastTour">
      <div className="title">
        <h4>Past Tournaments</h4>
        <h6>Tournaments i've played before</h6>
      </div>
      <section>
        <div className="photo">
          <img src={Logo} alt="Warzone" style={logoStyle} />
        </div>
        <div className="info">
          <h5>Monthly Duo Championship</h5>
          <h6>All Servers | All Platforms</h6>
        </div>
        <div className="sitotion">
          <h5>Finished</h5>
        </div>
        <div className="Prize">
          <h5>Prize Pool</h5>
          <h5>250 $</h5>
        </div>
        <div className="Rank">
          <h5>Rank</h5>
          <h5>85#</h5>
        </div>
      </section>
      <section>
        <div className="photo">
          <img src={Logo} alt="Warzone" />
        </div>
        <div className="info">
          <h5>Monthly Duo Championship</h5>
          <h6>All Servers | All Platforms</h6>
        </div>
        <div className="sitotion">
          <h5>Finished</h5>
        </div>
        <div className="Prize">
          <h5>Prize Pool</h5>
          <h5>250 $</h5>
        </div>
        <div className="Rank">
          <h5>Rank</h5>
          <h5>85#</h5>
        </div>
      </section>
      <div className="Seemore">
        <h4>View More</h4>
      </div>
    </div>
  );
};

export default PastTour;
