import React, { useContext, useRef, useEffect, useState } from "react";
import Coin from '../../../Images/coins.png';
import TestContext from "../../Context/testContext";
import AuthContext from "../../Context/Auth/authContext";
import Swal from "sweetalert2";
import "sweetalert2/src/sweetalert2.scss";

function useEffectSkipFirst(fn, arr) {
    const isFirst = useRef(true);
    useEffect(() => {
      if (isFirst.current) {
        isFirst.current = false;
        return;
      }
      fn();
    }, arr);
  }

const OverView = () => {
    const testContext = useContext(TestContext);
    const authContext = useContext(AuthContext);
    const { user, login, isAuthenticated, logout } = authContext;

    const [IdGame, setIdGame] = useState(null);
    const {
      GetActiveTournaments,
      activeTournamentData,
      registerGameUserData,
      RegisterUserForGame,
      SetRegisterTournament,
      err,
    } = testContext;
  const [GameId, setGameId] = useState(null);


    useEffect(() => {
        setIdGame(gameId);
        GetActiveTournaments(gameId);
      }, []);

      useEffectSkipFirst(() => {
        if (gameId) {
          GetActiveTournaments(gameId);
        }
      }, [gameId]);

    const SendInfo = (id) => {
        let gameData = {};
        gameData.GameId = gameId;
        gameData.TournamentId = parseInt(id);

        RegisterUserForGame(gameData);
      };

  return (
    <div className='OverView'>
        <div className="boxDetailes">
            <h3>Tournament Details</h3>
            <table>
                <tr>
                    <th><div className="box"><h5>Mode</h5><h5>SOLO</h5></div></th>
                    <th><div className="box"><h5>Date</h5><h5>SOLO</h5></div></th>
                    <th><div className="box"><h5>Entry Fee</h5><h5>SOLO <img src={Coin} alt="coin" /></h5></div></th>


                </tr>
                <tr>
                    <td><div className="box"><h5>Duration (Per Hour)</h5><h5>SOLO</h5></div></td>
                    <td><div className="box"><h5 className='prize'>Prize</h5><h5 className='prize'>20 $</h5></div></td>
                    <td><div className="box"><h5></h5><h5></h5></div></td>


                </tr>
            </table>
        </div>
            <button className='btnJoin'  id={item.id}
                    onClick={(e) => {
                      SendInfo(e.target.id);
                    }}>JOIN TOURNAMENT</button>
            <h5 className='note'>Note</h5>
            <div className="boxNote">
                <textarea name="" id="" cols="30" rows="10"></textarea>
                <button className='BtnDis'>JOIN FLC DISCORD</button>
            </div>
    </div>
  )
}

export default OverView
