import React from "react";

const Rules = () => {
  return (
    <div className="Rules">
      <h4>
        Only Ranked Matches are counted.
        <br />
        Only games played after you join the tournament are counted. <br />
        Smurfing (create new acconut to compete with) will result in an
        immediate and permanent ban from Repeat.gg and all winnings will be
        forfeited.
        <br /> All MMR's Allowed
        <br />
        Winner of digital prizes have 14 days after the tournament ends to claim
        the prize.
        <br />Winner of physical prizes have 7 days after the tournament ends to claim
        the prize.
      </h4>
    </div>
  );
};

export default Rules;
