import React, { useContext, useRef, useEffect, useState } from "react";
import TestContext from "../../../Context/testContext";
import Trophi from '../../../Images/tournament.png';
import PhotoGame from "../../../Images/icon-wz-white.png";
import Valorant from "../../../Images/valorant.png";
import Apex from "../../../Images/Apex.png";
import Dota from "../../../Images/dotalogo.png";
import Fifa from "../../../Images/whiteFifa.png";

function useEffectSkipFirst(fn, arr) {
   const isFirst = useRef(true);
   useEffect(() => {
     if (isFirst.current) {
       isFirst.current = false;
       return;
     }
     fn();
   }, arr);
 }

const ActiveTour = () => {

  const Call = "Call of Duty:Warzone";
  const Dota2 = "Dota 2";
  const FiFa23 = "FIFA 23";
  const ApexN = "Apex";
  const ValorantN = "Valorant";

  const testContext = useContext(TestContext);
  const {
   GetRegisterTournment,
   registerForThisGameData,
   err,
 } = testContext;

 useEffect(() => {
   GetRegisterTournment();
 }, []);



  return (
    <div className='ActiveTour'>
        <div className='title'>
            <h4>Active Tournments</h4>
            <h6>Tournments i'm playing now</h6>
        </div>
        {/* <div className='AboutInfo'>
            <img src={Trophi} alt=''/>
            <h4>No Current Tournments</h4>
            <h6>You Currently dont't have any Tournments</h6>
        </div> */}
        {registerForThisGameData
        ? registerForThisGameData.map((item) => (
        <div className='AcceptedTour'>
          <div className="photoGame">
            <img src={
                item.GameId == 1
                  ? PhotoGame
                  : item.GameId == 2
                  ? Dota
                  : item.GameId == 3
                  ? Fifa
                  : item.GameId == 4
                  ? Valorant
                  : item.GameId == 5
                  ? Apex
                  : null
              } alt="game" />
          </div>
          <div className="itemGame">
            <h4>{item.Title}</h4>
            <h6><span>{item.TournamentId}</span> | {item.ModeId}</h6>
          </div>
          <div className="btnStart">
            <button>START</button>
          </div>
           {/* <h4>{item.Title}</h4>
           <h4>{item.GameId}</h4>
           <h4>{item.StartDate}</h4>
           <h4>{item.Minutes}</h4> */}
           
           {/* <button>START</button> */}
        </div>)) :null}



       
    </div>
  )
}

export default ActiveTour
