import React, { useContext, useRef, useEffect, useState } from "react";
import Coin from "../../../Images/coins.png";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const PersonalInfo = () => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const {
    personalInfoData,
    switchBetweenUserAndPersonalInfo,
    SetSwitchUsers,
    usersEditByAdmin,
    UpdateUsersByAdmin,
    allUserFalconData,
    err,
  } = testContext;

  const [Firstname, setFirstname] = useState(null);
  const [Username, setUsername] = useState(null);
  const [Birthdate, setBirthdate] = useState(null);
  const [City, setCity] = useState(null);

  useEffectSkipFirst(() => {
    if (personalInfoData) {
      let newItem = {};
      setFirstname(personalInfoData.Firstname);
      setBirthdate(personalInfoData.Birthdate);
      setUsername(personalInfoData.Username);
      setCity(personalInfoData.City);
    }
  }, [personalInfoData]);

  const sendUserData = () => {
    let newUser = {};
    if (Firstname.length > 3 && Username.length > 3) {
      newUser.FirstName = Firstname;
      newUser.Birthdate = Birthdate;
      newUser.Username = Username;
      newUser.City = City;

      // UpdateUsersByAdmin(newUser);
      console.log("Test",);

    } else {
      console.log("empty");
    }

    // console.log("1", newUser);
  };

  return (
    <>
      {switchBetweenUserAndPersonalInfo ? null : (
        <div className="PersonalInfo">
          <div className="back">
            <h2>{personalInfoData.Username}</h2>
            <h2
              className="backStyle"
              onClick={(e) => {
                SetSwitchUsers(true);
              }}
            >
              Back
            </h2>
          </div>
          <div className="InputInfo">
            <h4>Personal Information</h4>
            <div className="prsData">
              <div className="item">
                <h5>Full Name</h5>
                <input
                  type="text"
                  value={Firstname}
                  onChange={(e) => setFirstname(e.target.value)}
                />
              </div>
              <div className="item">
                <h5>Date of Birth</h5>

                <input
                  type="Date"
                  value={Birthdate}
                  onChange={(e) => setBirthdate(e.target.value)}
                />
              </div>
              <div className="item">
                <h5>FLC ID (Username)</h5>
                <input
                  type="text"
                  value={Username}
                  onChange={(e) => setUsername(e.target.value)}
                />
              </div>
            </div>
            <div className="prsData">
              <div className="item">
                <h5>City</h5>
                <input
                  type="text"
                  value={City}
                  onChange={(e) => setCity(e.target.value)}
                />
              </div>

              <div className="item">
                <button
                  onClick={(e) => {
                    sendUserData();
                  }}
                >
                  Save Changes
                </button>
              </div>
            </div>
          </div>
          <div className="InputInfo">
            <div className="resetPass">
              <div className="itemPass">
                <h5>Current Password</h5>
                <input type="text" />
              </div>
              <div className="itemPass2">
                <button>Rest Password</button>
              </div>
            </div>
          </div>
          <div className="transaction">
            <h2>All Transactions</h2>
            <h5>Keep track of your earning and spending</h5>
          </div>
          <div className="transStyle">
            <div className="costData">
