        newItem.Goal = item.Goal;
        newItem.ProjectName = item.ProjectName;
        newItem.PrsNum = item.PrsNum;
        newItem.Nam = item.Nam;
        newItem.NamKhanevadegi = item.NamKhanevadegi;
        newItem.MiladiDate = item.MiladiDate;
        newItem.status = item.status;

        return newItem;
      });
      setEventData(newData);
