          <h5>Date of Birth</h5>
          <input type="date"/>
        </div>
        <div className="item">
          <h5>FLC ID (Username)</h5>
          <input type="text"/>
        </div>
      </div>
      <div className="buttonSetting">
        <button>Save Changes</button>
      </div>
    </div>
  );
};

export default Setiings;
