import React, { useContext, useRef, useEffect, useState } from "react";
import Coin from "../../../Images/coins.png";
import TestContext from "../../../Context/testContext";
import AuthContext from "../../../Context/Auth/authContext";
import Swal from "sweetalert2";
import "sweetalert2/src/sweetalert2.scss";

function useEffectSkipFirst(fn, arr) {
  const isFirst = useRef(true);
  useEffect(() => {
    if (isFirst.current) {
      isFirst.current = false;
      return;
    }
    fn();
  }, arr);
}

const PersonalInfo = () => {
  const testContext = useContext(TestContext);
  const authContext = useContext(AuthContext);
  const {
    personalInfoData,
    switchBetweenUserAndPersonalInfo,
    SetSwitchUsers,
    usersEditByAdmin,
    UpdateUsersByAdmin,
    SetEditUserByAdmin,
    err,
  } = testContext;

  const [Firstname, setFirstname] = useState(null);
  const [Username, setUsername] = useState(null);
  const [Birthdate, setBirthdate] = useState(null)
  const [IDUser, setIDUser] = useState(null)
  const [showMessage, setShowMessage] = useState(null);
  const [showMessage2, setShowMessage2] = useState(null);

  const [City, setCity] = useState(null);

  useEffectSkipFirst(() => {
    if (personalInfoData) {
      let newItem = {};
      setFirstname(personalInfoData.Firstname);
      setBirthdate(personalInfoData.Birthdate);
      setUsername(personalInfoData.Username);
      setCity(personalInfoData.City);
      setIDUser(personalInfoData.id);

    }
  }, [personalInfoData]);

  const sendUserData = () => {
    let newUser = {};
    if (Firstname.length > 3 && Username.length > 3) {
      newUser.FirstName = Firstname;
      newUser.Birthdate = Birthdate;
      newUser.Username = Username;
      newUser.City = City;
      newUser.id = IDUser;

      UpdateUsersByAdmin(newUser);
      console.log("Test",newUser);

    } else {
      console.log("empty");
    }

    // console.log("1", newUser);
  };


  useEffectSkipFirst(() => {
    if (showMessage) {
      Swal.fire({
        position: "center",
        icon: "success",
        title: "Your Information has been Updated",
        showConfirmButton: false,
        timer: 1500,
      });
      setShowMessage(false);
    }
  }, [showMessage]);

  useEffectSkipFirst(() => {
    if (usersEditByAdmin) {
        if(usersEditByAdmin.status == true){

          setShowMessage(true);
          SetEditUserByAdmin(null);
        } else {
          setShowMessage2(true);
          SetEditUserByAdmin(null);
        }

